pragma solidity ^0.5.12;
//商店 
contract Shop{
    address public myAds;
    mapping(address => uint) public balances;
    
    constructor() public{
        myAds = msg.sender;
    }
    
    //购买 
   function shop(address _to,uint _amount) public payable returns(string memory){
       if(_to == address(0x0)){
           return "接收者钱包地址无效 ";
       }
       //判断账户余额是否足够 
       if(balances[myAds] < _amount){
           return "余额不足 ";
       }
       
       //转账
       balances[myAds] -= _amount;
       
       balances[_to] += _amount;
       
       address(uint160(_to)).transfer(_amount);
       
       return "购买成功 ";
   }
   
      
}
