pragma solidity ^0.5.12;
contract tp{
    
    uint x;
    
    //人数变量 
    uint public persions;
    
    //投资金额 
    uint public amount;
    
    //庄家  
    address public banker;
    
    //资金池 
    mapping(address => uint) amountPool;
    //投资人账户 
    mapping(address => uint) invBalances;
    
    
    
    constructor() public payable{
        amount = msg.value;
        persions = 0;
        banker = msg.sender;
        x = 100000000000000000000;
    }
    
    //验证是否可投资  
    function investmentCheck(uint _amount) public view returns(string memory){
        if(msg.sender.balance < _amount){
             return "余额不足";
         }
         uint res = x - banker.balance;
         if(res < _amount){
             return "投资金额超出资金池上限";
         }
        if(_amount >= x || banker.balance >= x){
            return  "资金池金额已满";
        }
        return  "可投资";
    }
    
    //投资 
    function investment(uint _amount) public payable{
         //资金池填充金额，也就是把钱转给庄家 
        amountPool[banker] += _amount;
        invBalances[msg.sender] -= _amount;
         address(uint160(banker)).transfer(_amount);
    } 
    
    //庄家账户 
    function s1() public view returns(uint){
       return banker.balance; 
    }
    
    //合约调用者账户 
    function s2() public view returns(uint){
       return msg.sender.balance; 
    }
    
    //庄家钱包地址 
     function s3() public view returns(address){    
       return banker; 
    }
    

}
